'use strict';

var PHR = require('./performance');
var LOGS = require('./logger');

var WATCH = module.exports = {};

WATCH.setting = function(phrOpt, logwOpt, localLogOpt) {

	if (typeof phrOpt !== 'object') {
		throw new Error('not parse phrOpt');
	}

	if (typeof logwOpt !== 'object') {
		throw new Error('not params logwOpt');
	}

	LOGS.SET(logwOpt,localLogOpt);


	return PHR(phrOpt);

};

WATCH.logs = LOGS;